svg.script:
var svgNS = "http://www.w3.org/2000/svg";	var xlinkNS = "http://www.w3.org/1999/xlink";		var xcoord = 0;	var ycoord = 0;		var focusedItem = null;		if(document.captureEvents)	{		if(Event.MOUSEMOVE)		{			//NS 4, NS 6, Mozilla 0.9.x			document.captureEvents(Event.MOUSEMOVE);		}	}	document.onmousemove = function(e)	{		xcoord = e.clientX;		ycoord = e.clientY;				var x = xcoord; //- (item.getAttributeNS(null,"width") / 2);		var y = ycoord; //+ (item.getAttributeNS(null,"height") / 2);				if(focusedItem)		{			focusedItem.setAttributeNS(null,"x",x);			focusedItem.setAttributeNS(null,"y",y);
	}
}	//////////////////////////////////////////////		function createRect()	{		var newRect = document.createElementNS(svgNS,"rect");		newRect.setAttributeNS(null,"x",Math.random() * 250);		newRect.setAttributeNS(null,"y",Math.random() * 180 + 60);		newRect.setAttributeNS(null,"width",130);		newRect.setAttributeNS(null,"height",180);				newRect.setAttributeNS(null,"fill-opacity",90);				newRect.setAttributeNS(null,"onmousedown","setFocusedItem(this)");		newRect.setAttributeNS(null,"onmouseup","releaseFocusedItem(this)");						var red = Math.round(Math.random() * 255);		var green = Math.round(Math.random() * 255);		var blue = Math.round(Math.random() * 255);				newRect.setAttributeNS(null,"fill","rgb("+ red +","+ green+","+blue+")");		document.getElementById("firstGroup").appendChild(newRect);	}		function setFocusedItem(item)	{		focusedItem = item;
}		function releaseFocusedItem(item)	{		if(focusedItem == item)			focusedItem = null;
}			function createText() 	{		var newText = document.createElementNS(svgNS,"text");		newText.setAttributeNS(null,"x", 0); //Math.random() * 200 + 50);				newText.setAttributeNS(null,"y", 0); //Math.random() * 180 + 60);			newText.setAttributeNS(null,"font-size","13px");		newText.setAttributeNS(null,"text-anchor","middle");		newText.setAttributeNS(null,"fill-opacity",Math.random());				var red = Math.round(Math.random() * 255);		var green = Math.round(Math.random() * 255);		var blue = Math.round(Math.random() * 255);				newText.setAttributeNS(null,"fill","rgb("+ red +","+ green+","+blue+")");		var textNode = document.createTextNode("a new text");				newText.appendChild(textNode);		document.getElementById("firstGroup").appendChild(newText);	}		function createUseElement() 	{		var newUseEl = document.createElementNS(svgNS,"use");		newUseEl.setAttributeNS(null,"x", Math.random() * 200 + 50);		newUseEl.setAttributeNS(null,"y", Math.random() * 180 + 80);		newUseEl.setAttributeNS(xlinkNS,"href","#mySymbol");		newUseEl.setAttributeNS(null,"fill-opacity",Math.random());				document.getElementById("firstGroup").appendChild(newUseEl);	}
